const decode = (data) => {
    let newString = "";
    Array.from(data).forEach((char) => {
        if(map.hasOwnProperty(char)) newString += map[char];
        else if(Object.values(map).includes(char)) {
            newString += Object.keys(map)[Object.values(map).indexOf(char)];
        }
        else newString += char;
    })
    return newString;
}

